mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate, category
from ssafy_member
where userid = #{userid}

select token
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
set token = #{token}
where userid = #{userid}

update ssafy_member
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}

mapper.select:
select id,aptCode,bookmarkedTime
from bookmark
where userid = #{userid}

mapper.delete:
delete
from bookmark
where userId = #{userId}
and aptCode = #{aptCode}
